import style from "./Card.module.css";
import { Link } from "react-router-dom";
import { addFav, removeFav } from "../../redux/actions"
import { connect } from 'react-redux';
import { useState } from "react";
import { useEffect } from "react";


function Card(props) {

   const [isFav, setIsFav] = useState(false);

   const handleFavorite = (event) => {
      event.preventDefault();
      if (isFav) {
        setIsFav(false);
        props.removeFavCard(props.id);
      } else {
        setIsFav(true);
        props.addFavCard(props);
      }
   };

   useEffect(() => {
      props.myFavoriteCard.forEach((fav) => {
         if (fav.id === props.id) {
            setIsFav(true);
         }
      });
   }, [props.myFavoriteCard]);

   return (
      <div className={style.wrapperCard}>
        <div className={style.tagCard}>Tag</div>
        <button className={`${style.button} ${isFav ? style.buttonFavorite : style.buttonUnfavorite}`} onClick={handleFavorite}>
          ❤️
        </button>
        <img className={style.imgCard} src={props.image} alt="" />
        <div className={style.contentCard}>
          <h3 className={style.nameCard}>{props.name}</h3>
          <h2 className={style.tag}>Estado: {props.status}</h2>
          <h2>Especie: {props.species}</h2>
          <h2>Género: {props.gender}</h2>
          <h2>From: {props.origin}</h2>
        </div>
        <button className={style.btn} onClick={() => props.onClose(props.id)}>
          X
        </button>
      </div>
    );

}

export function mapDispatchToProps(dispatch) {
   return {
      addFavCard: (char) => dispatch(addFav(char)),
      removeFavCard: (id) => dispatch(removeFav(id))
   }
}

export function mapStateToProps(state) {
   return {
      myFavoriteCard: state.myFavorites
   }
}

export default connect(mapStateToProps, mapDispatchToProps)(Card);
